// -------------------- MODALS --------------------
function openAddEntryModal() {
    const modal = document.getElementById("addEntryModal");
    modal.classList.remove("hidden");
    setTimeout(() => {
        modal.classList.add("bg-opacity-50");
        const inner = modal.querySelector("div");
        inner.classList.remove("scale-95", "opacity-0");
        inner.classList.add("scale-100", "opacity-100");
    }, 10);
}

function closeAddEntryModal() {
    const modal = document.getElementById("addEntryModal");
    const inner = modal.querySelector("div");
    inner.classList.remove("scale-100", "opacity-100");
    inner.classList.add("scale-95", "opacity-0");
    modal.classList.remove("bg-opacity-50");
    setTimeout(() => modal.classList.add("hidden"), 300);
}

function openScannerModal() {
    const modal = document.getElementById("scannerModal");
    modal.classList.remove("hidden");
    setTimeout(() => {
        modal.classList.add("bg-opacity-50");
        const inner = modal.querySelector("div");
        inner.classList.remove("scale-95", "opacity-0");
        inner.classList.add("scale-100", "opacity-100");
    }, 10);
    startScanner();
}

function closeScannerModal() {
    const modal = document.getElementById("scannerModal");
    modal.classList.add("hidden");
    const inner = modal.querySelector("div");
    inner.classList.add("scale-95", "opacity-0");
    if (codeReader) codeReader.reset();
}

// -------------------- ISBN --------------------
function formatISBN(input) {
    let value = input.value.replace(/\D/g, '');
    if (!value.startsWith("978") && value.length > 0) value = "978" + value;
    input.value = value;
}

function validateISBN() {
    const isbn = document.getElementById("isbn").value.replace(/\D/g, '');
    if (!isbn.startsWith("978") || isbn.length !== 13) {
        Swal.fire({
            icon: "error",
            title: "Invalid ISBN",
            text: "ISBN must be 13 digits starting with 978",
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 3000
        });
        return false;
    }
    return true;
}

// -------------------- FETCH ENTRIES --------------------
let locationUpdateTimeout, searchTimeout;

async function fetchEntries(search = "") {
    try {
        let url = "/api/entries";
        if (search) url += `?search=${encodeURIComponent(search)}`;
        const res = await fetch(url);
        const data = await res.json();

        const tbody = document.getElementById("entriesTableBody");
        tbody.innerHTML = "";

        data.forEach(entry => {
            const tr = document.createElement("tr");
            tr.className = "hover:bg-gray-50 transition-colors duration-150";
            tr.innerHTML = `
                <td class="px-6 py-4 text-sm font-medium">${entry.name}</td>
                <td class="px-6 py-4 text-sm text-gray-500">${entry.isbn}</td>
                <td class="px-6 py-4 text-sm text-gray-500">
                    <input type="text"
                           value="${entry.location || ''}"
                           class="location-input border rounded px-2 py-1 text-sm w-full"
                           data-isbn="${entry.isbn}"
                           oninput="debouncedUpdateLocation('${entry.isbn}', this.value)">
                </td>
                <td class="px-6 py-4 text-sm text-gray-500">${entry.author}</td>
                <td class="px-6 py-4 text-sm text-gray-500">
                    ${entry.summary && entry.summary.length > 30 ?
                        `<button onclick="openDescriptionModal('${entry.summary.replace(/'/g,"\\'")}')" class="text-blue-500 hover:underline">Read description</button>` :
                        entry.summary || "-"
                    }
                </td>
                <td class="px-6 py-4 text-sm text-gray-500">${entry.pages}</td>
                <td class="px-6 py-4 text-sm text-gray-500">${entry.language}</td>
                <td class="px-6 py-4 text-sm text-gray-500">${entry.publishedDate}</td>
                <td class="px-6 py-4">
                    <img src="${entry.imageUrl || 'http://static.photos/books/200x200'}"
                         alt="Book cover"
                         class="w-10 h-14 object-cover rounded hover:scale-105 transition-transform duration-200 cursor-pointer"
                         onclick="openImageModal('${entry.imageUrl || 'http://static.photos/books/200x200'}')">
                </td>
                <td class="px-6 py-4 text-sm text-gray-500">
                    <button onclick="deleteEntryByISBN('${entry.isbn}')" class="text-red-500 hover:text-red-700">
                        <i data-feather="trash-2" class="w-4 h-4"></i>
                    </button>
                </td>
            `;
            tbody.appendChild(tr);
        });

        feather.replace();
    } catch (err) {
        console.error("Error fetching entries:", err);
    }
}

// -------------------- DELETE ENTRY --------------------
async function deleteEntryByISBN(isbn) {
    if (!isbn) return Swal.fire({ icon: "error", title: "Error", text: "Invalid ISBN" });

    const confirm = await Swal.fire({
        title: "Delete Book",
        text: "Are you sure you want to delete this book?",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, delete it!"
    });

    if (confirm.isConfirmed) {
        try {
            const res = await fetch(`/api/entries/isbn/${encodeURIComponent(isbn)}`, {
                method: "DELETE",
                headers: { "Content-Type": "application/json" }
            });
            const data = await res.json();
            Swal.fire({ icon: "success", title: "Deleted!", text: data.message, toast: true, position: "top-end", showConfirmButton: false, timer: 3000 });
            fetchEntries();
        } catch (err) {
            console.error(err);
            Swal.fire({ icon: "error", title: "Error", text: "Failed to delete book.", toast: true, position: "top-end", showConfirmButton: false, timer: 3000 });
        }
    }
}

// -------------------- UPDATE LOCATION --------------------
function debouncedUpdateLocation(isbn, value) {
    clearTimeout(locationUpdateTimeout);
    locationUpdateTimeout = setTimeout(() => updateLocation(isbn, value), 1000);
}

async function updateLocation(isbn, location) {
    try {
        const res = await fetch(`/api/entries/${encodeURIComponent(isbn)}/location`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ location })
        });
        const data = await res.json();
        if (data.error) throw data.error;
        Swal.fire({ icon: "success", title: "Location Updated", text: "Saved successfully", toast: true, position: "top-end", showConfirmButton: false, timer: 2000 });
    } catch (err) {
        console.error(err);
        Swal.fire({ icon: "error", title: "Update Failed", text: "Could not update location", toast: true, position: "top-end", showConfirmButton: false, timer: 3000 });
    }
}

// -------------------- ADD ENTRY --------------------
document.getElementById("isbnForm").addEventListener("submit", async (e) => {
    e.preventDefault();
    if (!validateISBN()) return;

    const isbn = document.getElementById("isbn").value.replace(/\D/g, '');
    const location = document.getElementById("location").value;

    try {
        const res = await fetch("/api/entries", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ isbn, location })
        });
        const data = await res.json();
        if (data.error) throw data.error;

        Swal.fire({ icon: "success", title: "Success", text: data.message, toast: true, position: "top-end", showConfirmButton: false, timer: 2000 });
        fetchEntries();
        closeAddEntryModal();
        document.getElementById("isbn").value = "";
        document.getElementById("location").value = "";
    } catch (err) {
        console.error("Error submitting ISBN:", err);
        Swal.fire({ icon: "error", title: "Error", text: err.error || "Could not add book", toast: true, position: "top-end", showConfirmButton: false, timer: 3000 });
    }
});

// -------------------- SEARCH --------------------
function searchEntries() {
    clearTimeout(searchTimeout);
    searchTimeout = setTimeout(() => {
        const value = document.getElementById("searchInput").value.trim();
        fetchEntries(value);
    }, 300);
}

// -------------------- MODALS FOR IMAGE & DESCRIPTION --------------------
function openImageModal(url) {
    const modal = document.createElement("div");
    modal.className = "fixed inset-0 bg-black bg-opacity-0 flex items-center justify-center z-50 p-4 transition-opacity duration-300";
    modal.innerHTML = `
        <div class="relative max-w-4xl w-full transform transition-all duration-300 scale-95 opacity-0">
            <img src="${url}" alt="Book cover" class="w-full max-h-[90vh] object-contain">
            <button onclick="this.parentElement.parentElement.remove()" class="absolute top-4 right-4 text-white text-2xl hover:text-gray-300">&times;</button>
        </div>
    `;
    document.body.appendChild(modal);
    setTimeout(() => {
        modal.classList.add("bg-opacity-75");
        const inner = modal.querySelector("div");
        inner.classList.remove("scale-95", "opacity-0");
        inner.classList.add("scale-100", "opacity-100");
    }, 10);
}

function openDescriptionModal(desc) {
    const modal = document.createElement("div");
    modal.className = "fixed inset-0 bg-black bg-opacity-0 flex items-center justify-center z-50 p-4 transition-opacity duration-300";
    modal.innerHTML = `
        <div class="bg-white rounded-lg p-6 max-w-2xl w-full max-h-[80vh] overflow-y-auto transform transition-all duration-300 scale-95 opacity-0">
            <div class="flex justify-between items-center mb-4">
                <h3 class="text-lg font-medium">Book Description</h3>
                <button onclick="this.parentElement.parentElement.parentElement.remove()" class="text-gray-400 hover:text-gray-500">
                    <i data-feather="x" class="w-5 h-5"></i>
                </button>
            </div>
            <p class="text-gray-700 whitespace-pre-wrap">${desc}</p>
        </div>
    `;
    document.body.appendChild(modal);
    setTimeout(() => {
        modal.classList.add("bg-opacity-75");
        const inner = modal.querySelector("div");
        inner.classList.remove("scale-95", "opacity-0");
        inner.classList.add("scale-100", "opacity-100");
    }, 10);
    feather.replace();
}

// -------------------- ZXING SCANNER --------------------
const codeReader = new ZXing.BrowserMultiFormatReader();
let selectedDeviceId = null;

async function startScanner() {
    try {
        const devices = await codeReader.listVideoInputDevices();
        if (!devices.length) return Swal.fire("No camera found", "Please connect a camera", "error");
        selectedDeviceId = devices[0].deviceId;
        const video = document.getElementById("scanner");
        await codeReader.decodeFromVideoDevice(selectedDeviceId, video, async (result, err) => {
            if (result) {
                const text = result.getText();
                if (/^97[89]\d{10}$/.test(text)) {
                    const res = await fetch("/api/entries", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ isbn: text, location: "N/A" })
                    });
                    const data = await res.json();
                    if (data.error) {
                        Swal.fire({ toast: true, position: "top-end", icon: "error", title: data.error, showConfirmButton: false, timer: 2000 });
                    } else {
                        Swal.fire({ toast: true, position: "top-end", icon: "success", title: `Added: ${data.name}`, showConfirmButton: false, timer: 2000 });
                        fetchEntries();
                    }
                } else {
                    Swal.fire({ toast: true, position: "top-end", icon: "warning", title: "Not a valid ISBN-13", showConfirmButton: false, timer: 2000 });
                }
            }
        });
    } catch (err) {
        console.error(err);
        Swal.fire("Error", err.message, "error");
    }
}

// -------------------- INIT --------------------
document.addEventListener("DOMContentLoaded", () => {
    fetchEntries();
    setInterval(fetchEntries, 10000);

    document.getElementById("searchInput").addEventListener("input", searchEntries);
    document.getElementById("searchInput").addEventListener("keypress", (e) => { if (e.key === "Enter") searchEntries(); });
    document.getElementById("isbn").addEventListener("input", (e) => formatISBN(e.target));

    AOS.init({ duration: 600, easing: "ease-out-quad", once: true });
    feather.replace();
});
